import {
  PropertySectionContainer,
  PropertySectionImage,
  PropertySectionText,
  PropertySectionTitle,
} from "./styles";

type bannerSectionItems = {
  title: string;
  price?: string;
  image?: string;
  rooms?: string;
  baths?: string;
};

export default function PropertySection({
  price,
  title,
  image,
  rooms,
  baths,
}: bannerSectionItems) {
  const numberFormat = (value: any) =>
    new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "USD",
    }).format(value);
  return (
    <PropertySectionContainer>
      <PropertySectionImage src={image}></PropertySectionImage>
      <PropertySectionTitle>
        {title.length > 30 ? `${title?.substring(0, 30)}...` : title}
      </PropertySectionTitle>

      <PropertySectionText>PRICE: {numberFormat(price)}</PropertySectionText>
      <PropertySectionText>ROOMS: {rooms}</PropertySectionText>
      <PropertySectionText>BATHS: {baths}</PropertySectionText>
    </PropertySectionContainer>
  );
}
