import React, { useMemo } from "react";
import { useCallback, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { bayutUrl, fetchApi } from "../../utiles/fetchApi";
import Carousel from "./carousel";
import { Container, SubText, TextContainer, Title } from "./styles";

type PropertyType = {
  id: string;
};

export default function PropertyDetails() {
  const { id } = useParams<PropertyType>();

  const [title, setTitle] = useState();
  const [baths, setBaths] = useState();
  const [rooms, setRooms] = useState();
  const [price, setPrice] = useState();
  const [description, setDescription] = useState();
  const [photos, setPhotos] = useState([{}]);
  const [amenitites, setAmenitites] = useState([]);

  const getResults = useCallback(async () => {
    const results = await fetchApi(
      `${bayutUrl}/properties/detail?externalID=${id}`
    );
    const searchData = results;
    setTitle(searchData.title);
    setRooms(searchData.baths);
    setPrice(searchData.price);
    setBaths(searchData.baths);
    setDescription(searchData.description);
    setPhotos(searchData.photos);
    setAmenitites(searchData.amenitites);
    console.log(searchData);
  }, [id]);

  useEffect(() => {
    getResults();
  }, [getResults]);

  const images = [
    {
      title: "San Francisco – Oakland Bay Bridge, United States",
      url: "https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60",
    },
    {
      title: "Bird",
      url: "https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60",
    },
    {
      title: "Bali, Indonesia",
      url: "https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250",
    },
    {
      title: "Goč, Serbia",
      url: "https://images.unsplash.com/photo-1512341689857-198e7e2f3ca8?auto=format&fit=crop&w=400&h=250&q=60",
    },
  ];

  return (
    <Container>
      <Title>PropertyDetails </Title>
      <Carousel
        photos={images.map((property) => ({
          title: property.title,
          url: property.url,
        }))}
      ></Carousel>
      <TextContainer>
        <SubText>title={title}</SubText>
        <SubText>baths={baths}</SubText>
        <SubText>price={Number(price)}</SubText>
        <SubText>rooms={rooms}</SubText>
        <SubText>description={description}</SubText>
      </TextContainer>
      {amenitites.map((array) => {
        return <SubText>{array}</SubText>;
      })}
    </Container>
  );
}
