import { useCallback, useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import { bayutUrl, fetchApi } from "../../utiles/fetchApi";
import Carousel from "./carousel";
import { Container, SubText, TextContainer, Title } from "./styles";
import * as React from "react";
import { useTheme } from "@mui/material/styles";
import MobileStepper from "@mui/material/MobileStepper";

import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import KeyboardArrowLeft from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import styled from "styled-components";

const PropertyImageContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 0 3vw 4vw;
  justify-content: flex-start;
  align-items: center;
  height: 100%;
  width: 50%;
  box-sizing: border-box;
`;

const PropertyImage = styled.img`
  margin: 1vw;
  border-radius: 4px;
  border-style: solid;
  width: 25vw;
  height: 20vw;
  object-fit: cover;
`;

export default function PropertyDetails() {
  const [searchParams] = useSearchParams();

  const id = searchParams.get("id");

  const [property, setProperty] = useState({
    title: "default 1",
    baths: "NA",
    rooms: "NA",
    price: "NA",
    image: "NA",
  });

  const getResults = useCallback(async () => {
    const results = await fetchApi(
      `${bayutUrl}/properties/detail?externalID=${id}`
    );
    const searchData = results;
    setProperty({
      title: results.title,
      image: results.coverPhoto.url,
      baths: results.baths,
      rooms: results.rooms,
      price: results.price,
    });
    console.log(searchData);
  }, [id]);

  useEffect(() => {
    getResults();
  }, [getResults]);

  const images = [
    {
      label: "San Francisco – Oakland Bay Bridge, United States",
      img: "https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60",
    },
    {
      label: "Bird",
      img: "https://images.unsplash.com/photo-1538032746644-0212e812a9e7?auto=format&fit=crop&w=400&h=250&q=60",
    },
    {
      label: "Bali, Indonesia",
      img: "https://images.unsplash.com/photo-1537996194471-e657df975ab4?auto=format&fit=crop&w=400&h=250",
    },
    {
      label: "Goč, Serbia",
      img: "https://images.unsplash.com/photo-1512341689857-198e7e2f3ca8?auto=format&fit=crop&w=400&h=250&q=60",
    },
  ];

  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = images.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  return (
    <Container>
      <Title>PropertyDetails </Title>
      <PropertyImageContainer>
        <Typography>{images[activeStep].label}</Typography>
        <PropertyImage src={images[activeStep].img}></PropertyImage>
        <MobileStepper
          steps={maxSteps}
          position="static"
          activeStep={activeStep}
          nextButton={
            <Button
              size="small"
              onClick={handleNext}
              disabled={activeStep === maxSteps - 1}
            >
              Next
              {theme.direction === "rtl" ? (
                <KeyboardArrowLeft />
              ) : (
                <KeyboardArrowRight />
              )}
            </Button>
          }
          backButton={
            <Button
              size="small"
              onClick={handleBack}
              disabled={activeStep === 0}
            >
              {theme.direction === "rtl" ? (
                <KeyboardArrowRight />
              ) : (
                <KeyboardArrowLeft />
              )}
              Back
            </Button>
          }
        />
      </PropertyImageContainer>
      <TextContainer>
        <SubText>title={property.title}</SubText>
        <SubText>baths={property.baths}</SubText>
        <SubText>price={Number(property.price)}</SubText>
        <SubText>rooms={property.rooms}</SubText>
        <SubText>image={property.image}</SubText>
      </TextContainer>
    </Container>
  );
}
