import React, { useCallback, useEffect, useState } from "react";
import { Container, Title, InnerContainer } from "./styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import { Button } from "@material-ui/core";
import useQuery from "../../utiles/searchparams";
import PropertySection from "../Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { Properties, rentOptions, saleOptions } from "../../utiles/fetchApi";

type PropertyType = {
  title?: string;
  price?: string;
  coverPhoto: { url: string };
  rooms?: string;
  baths?: string;
};

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);
  const [propertyArray, setPropertyArray] = useState([]);
  const query = useQuery();

  let api = Properties();

  if (query.get("purpose") === "for-sale") {
    api = saleOptions;
  }
  if (query.get("purpose") === "for-rent") {
    api = rentOptions;
  }

  const getData = useCallback(() => {
    axios
      .request(api)
      .then(function (response) {
        setPropertyArray(response.data.hits);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, [api]);

  useEffect(() => {
    getData();
  }, [getData]);

  return (
    <>
      <Container>
        <Button onClick={() => setSearchFilters((prevFilters) => !prevFilters)}>
          <Title>Search Properities</Title>
          <FilterListIcon />
        </Button>
        {searchFilters && (
          <>
            <SearchFilters purpose={query.get("purpose")}></SearchFilters>
          </>
        )}
      </Container>
      <InnerContainer>
        {propertyArray.map((property: PropertyType) => (
          <>
            {console.log(propertyArray)}
            <PropertySection
              image={property.coverPhoto.url}
              title={property.title}
              price={property.price}
              rooms={property.rooms}
              baths={property.baths}
            ></PropertySection>
          </>
        ))}
      </InnerContainer>
    </>
  );
}

export default Search;
