import React, { useCallback, useEffect, useState } from "react";
import {
  Container,
  Title,
  InnerContainer,
  FilterButton,
  NoResults,
  NoResultsContainer,
  NoResultsTitle,
} from "./styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import PropertySection from "../Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { Properties } from "../../utiles/fetchApi";
import { useSearchParams } from "react-router-dom";
import images from "../../assets";

type PropertyType = {
  title?: string;
  price?: string;
  coverPhoto: { url: string };
  rooms?: string;
  baths?: string;
  id: number;
};

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);
  const [propertyArray, setPropertyArray] = useState([]);
  const [searchParams] = useSearchParams();

  let api = Properties();

  const getData = useCallback(() => {
    axios
      .request(api)
      .then(function (response) {
        setPropertyArray(response.data.hits);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, [api]);

  useEffect(() => {
    getData();
  }, [getData]);

  return (
    <>
      <Container>
        <FilterButton
          onClick={() => setSearchFilters((prevFilters) => !prevFilters)}
        >
          <Title>Search Properities</Title>
          <FilterListIcon />
        </FilterButton>
        {searchFilters && (
          <>
            <SearchFilters
              purpose={searchParams.get("purpose")}
            ></SearchFilters>
          </>
        )}
      </Container>

      {propertyArray.map((property: PropertyType) => (
        <InnerContainer>
          <PropertySection
            image={property.coverPhoto.url}
            title={property.title}
            price={property.price}
            rooms={property.rooms}
            baths={property.baths}
            link={`${property.id}`}
          ></PropertySection>
        </InnerContainer>
      ))}
      {propertyArray.length === 0 && (
        <NoResultsContainer>
          <NoResults src={images.noresults}></NoResults>
          <NoResultsTitle> No Results Found</NoResultsTitle>
        </NoResultsContainer>
      )}
    </>
  );
}

export default Search;
