import { IconButton, Menu, MenuItem, Toolbar } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import React, { useEffect, useRef, useState } from "react";
import images from "../../assets";
import { Logo, Navbar } from "./styles";

function NavBar() {
  const ref = useRef();

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const checkIfClickedOutside = (e: any | undefined) => {
      // If the menu is open and the clicked target is not within the menu,
      // then close the menu
      if (isMenuOpen && ref.current && !ref.current.contains(e.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", checkIfClickedOutside);

    return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", checkIfClickedOutside);
    };
  }, [isMenuOpen]);

  return (
    <>
      <Navbar>
        <Toolbar>
          <Logo src={images.houseLogo}></Logo>
          <IconButton
            aria-controls={open ? "demo-positioned-menu" : undefined}
            aria-haspopup="true"
            aria-expanded={open ? "true" : undefined}
            onClick={handleClick}
            onDoubleClick={handleClose}
          >
            <MenuIcon />
            <Menu
              aria-labelledby="demo-positioned-button"
              anchorEl={anchorEl}
              open={open}
              onClose={handleClose}
              anchorOrigin={{
                vertical: "top",
                horizontal: "left",
              }}
              transformOrigin={{
                vertical: "bottom",
                horizontal: "right",
              }}
            >
              {isMenuOpen && (
                <>
                  <MenuItem>Home</MenuItem>
                  <MenuItem>Rent</MenuItem>
                  <MenuItem>Buy</MenuItem>
                </>
              )}{" "}
            </Menu>
          </IconButton>
        </Toolbar>
      </Navbar>
    </>
  );
}

export default NavBar;
