import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import ListItemText from "@mui/material/ListItemText";
import Select, { SelectChangeEvent } from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";
import { useState } from "react";
import { Container, FilterContainer } from "./styles";
import React from "react";
import { filterData, getFilterValues } from "../../utiles/filterData";
import useQuery from "../../utiles/searchparams";
import { useLocation, useNavigate, useNavigation } from "react-router-dom";

function SearchFilters({ purpose }: any) {
  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
        width: 100,
      },
    },
  };

  const [filterItem, setFilter] = useState<string[]>([]);
  const [fitlers, setFitlers] = useState(filterData);
  const router = useLocation();

  const searchProperties = (filterValues: any) => {
    const path = router.pathname;
    const query = router;
    const values = getFilterValues(filterValues);

    values.forEach((item) => {
      query.state[item.name] = item.value;
    });
    router.state({ pathname: path, query });
  };

  const handleChange = (event: any) => {
    setFilter(event.target.value);
  };

  return (
    <Container>
      <div>
        {purpose ? (
          <h3> Searching Properties {purpose}</h3>
        ) : (
          <h3> Searching Properties</h3>
        )}
      </div>
      <FilterContainer>
        {fitlers.map((filter) => (
          <FormControl sx={{ m: 1, width: 160 }}>
            <InputLabel>{filter.placeholder}</InputLabel>
            <Select
              value={filterItem}
              onChange={handleChange}
              input={<OutlinedInput />}
              MenuProps={MenuProps}
            >
              {filter.items.map((item) => (
                <MenuItem value={item.value}>
                  <ListItemText primary={item.name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        ))}
      </FilterContainer>
    </Container>
  );
}
export default SearchFilters;
