import { IconButton, Toolbar } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import React, { useEffect, useRef, useState } from "react";
import images from "../../assets";
import { Logo, Navbar } from "./styles";

function NavBar() {
  const ref = useRef();

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const checkIfClickedOutside = (e: { target: any }) => {
      // If the menu is open and the clicked target is not within the menu,
      // then close the menu
      if (isMenuOpen && ref.current && !ref.current.contains(e.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", checkIfClickedOutside);

    return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", checkIfClickedOutside);
    };
  }, [isMenuOpen]);

  return (
    <>
      <Navbar>
        <Toolbar>
          <Logo src={images.houseLogo}></Logo>
          <IconButton onClick={() => setIsMenuOpen((oldState) => !oldState)}>
            <MenuIcon />
          </IconButton>
          {isMenuOpen && (
            <ul className="list">
              <li className="list-item">dropdown option 1</li>
              <li className="list-item">dropdown option 2</li>
              <li className="list-item">dropdown option 3</li>
              <li className="list-item">dropdown option 4</li>
            </ul>
          )}
        </Toolbar>
      </Navbar>
    </>
  );
}

export default NavBar;
