import React, { useCallback, useEffect, useState } from "react";
import {
  Container,
  Title,
  InnerContainer,
  FilterButton,
  NoResults,
  NoResultsContainer,
  NoResultsTitle,
} from "./styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import PropertySection from "../Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { options, Properties } from "../../utiles/fetchApi";
import { useSearchParams } from "react-router-dom";
import images from "../../assets";

type PropertyType = {
  title?: string;
  price: number;
  coverPhoto: { url: string };
  rooms?: string;
  baths?: string;
  id: number;
};

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);
  const [propertyArray, setPropertyArray] = useState([]);
  const [searchParams] = useSearchParams();

  const purpose = searchParams.get("purpose") || "for-sale";
  const rentFrequency = searchParams.get("rentFrequency") || "yearly";
  const priceMin = searchParams.get("priceMin") || "0";
  const priceMax = searchParams.get("priceMax") || "1000000";
  const roomsMin = searchParams.get("roomsMin") || "0";
  const bathsMin = searchParams.get("bathsMin") || "0";
  const sort = searchParams.get("sort") || "price-desc";
  const areaMax = searchParams.get("areaMax") || "35000";
  const categoryExternalID = searchParams.get("categoryExternalID") || "4";
  const furnishingStatus =
    searchParams.get("furnishingStatus") || "unfurnished";

  const getData = async () => {
    const response = await fetch(
      `https://bayut.p.rapidapi.com/properties/list?locationExternalIDs=5002%2C6020&purpose=${purpose}&hitsPerPage=25&page=0&lang=en&sort=${sort}&rentFrequency=${rentFrequency}&categoryExternalID=${categoryExternalID}&priceMin=${priceMin}&priceMax=${priceMax}&areaMax=${areaMax}&roomsMin=${roomsMin}&bathsMin=${bathsMin}&furnishingStatus=${furnishingStatus}`,
      options
    );
    const data = await response.json();
    setPropertyArray(data);
  };

  useEffect(() => {
    getData();
  }, [getData]);

  return (
    <>
      <Container>
        <FilterButton
          onClick={() => setSearchFilters((prevFilters) => !prevFilters)}
        >
          <Title>Search Properities</Title>
          <FilterListIcon />
        </FilterButton>
        {searchFilters && (
          <>
            <SearchFilters
              purpose={searchParams.get("purpose")}
            ></SearchFilters>
          </>
        )}
      </Container>

      {propertyArray.map((property: PropertyType) => (
        <InnerContainer>
          <PropertySection
            image={property.coverPhoto.url}
            title={property.title}
            price={property.price}
            rooms={property.rooms}
            baths={property.baths}
            link={`${property.id}`}
          ></PropertySection>
        </InnerContainer>
      ))}
      {propertyArray.length === 0 && (
        <NoResultsContainer>
          <NoResults src={images.noresults}></NoResults>
          <NoResultsTitle> No Results Found</NoResultsTitle>
        </NoResultsContainer>
      )}
    </>
  );
}

export default Search;
