import React, { useEffect, useState } from "react";
import { Container, Title } from "../../pages/Search/styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import { Button } from "@material-ui/core";
import useQuery from "../../utiles/searchparams";
import PropertySection from "../../pages/Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { options, rentOptions, saleOptions } from "../../utiles/fetchApi";

type propertyType = { property?: any };

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);
  const query = useQuery();
  const [propertyArray, setPropertyArray] = useState([]);

  let api = options;

  if (query.get("for-sale")) {
    api = saleOptions;
  }
  if (query.get("for-rent")) {
    api = rentOptions;
  }

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    axios
      .request(api)
      .then(function (response) {
        setPropertyArray(response.data.hits);
      })
      .catch(function (error) {
        console.error(error);
      });
  };

  return (
    <>
      <Container>
        <Button onClick={() => setSearchFilters((prevFilters) => !prevFilters)}>
          <Title>Search Properities</Title>
          <FilterListIcon />
        </Button>
        {searchFilters && (
          <>
            <SearchFilters property={query.get("property")}></SearchFilters>
          </>
        )}
      </Container>
      <>
        {propertyArray.map(({ property }: propertyType) => (
          <PropertySection
            title={property.title ?? 0}
            baths={property.baths}
            rooms={property.rooms}
            price={property.price}
            image={property.image}
          ></PropertySection>
        ))}
      </>
    </>
  );
}

export default Search;
