import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import ListItemText from "@mui/material/ListItemText";
import Select, { SelectChangeEvent, SelectProps } from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";
import { useState } from "react";
import { Container, FilterContainer } from "./styles";
import React from "react";
import { filterData, getFilterValues } from "../../utiles/filterData";
import useQuery from "../../utiles/searchparams";
import { useLocation, useNavigate, useNavigation } from "react-router-dom";

function SearchFilters({ purpose }: any) {
  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
        width: 100,
      },
    },
  };

  const [filters, setFitlers] = useState(filterData);

  const [filterValues, setFilterValues] = useState(
    filters.reduce(
      (acc, item) => ({
        ...acc,
        [item.queryName]: undefined,
      }),
      {} as Record<typeof filters[number]["queryName"], string | undefined>
    )
  );

  const handleChange =
    (queryName: string): SelectProps["onChange"] =>
    (e) => {
      setFilterValues((previous) => ({
        ...previous,
        [queryName]: e.target.value as string,
      }));
    };

  // const router = useLocation();
  // const searchProperties = (filterValues: any) => {
  //   const path = router.pathname;
  //   const query = router;
  //   const values = getFilterValues(filterValues);

  //   values.forEach((item) => {
  //     query.state[item.name] = item.value;
  //   });
  //   router.state({ pathname: path, query });
  // };

  // const [params, setParams] = useState({
  //   locationExternalIDs: "5002,6020",
  //   purpose: "for-rent",
  //   page: "0",
  //   lang: "en",
  //   sort: "city-level-score",
  //   categoryExternalID: "4",
  //   roomsMax: "",
  //   rentFrequency: "",
  //   minPrice: "",
  //   maxPrice: "",
  //   areaMax: "",
  //   roomsMin: "",
  //   bathsMin: "",
  //   furnishingStatus: "",
  // });

  // const updateLocationExternalIDs = (event: any) => {
  //   setParams((currentState) => {
  //     return { ...currentState, locationExternalIDs: event.target.value };
  //   });
  // };

  return (
    <Container>
      <div>
        {purpose ? (
          <h3> Searching Properties {purpose}</h3>
        ) : (
          <h3> Searching Properties</h3>
        )}
      </div>
      <FilterContainer>
        {filters.map(({ items, placeholder, queryName }, index) => (
          <FormControl key={index} sx={{ m: 1, width: 160 }}>
            <InputLabel>{placeholder}</InputLabel>
            <Select
              value={filterValues[queryName]}
              onChange={handleChange(queryName)}
              input={<OutlinedInput />}
              MenuProps={MenuProps}
            >
              {items.map(({ value, name }) => (
                <MenuItem value={value}>
                  <ListItemText primary={name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        ))}
      </FilterContainer>
    </Container>
  );
}
export default SearchFilters;
