import React, { useEffect, useState } from "react";
import { Container, Title, InnerContainer } from "../../pages/Search/styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import { Button } from "@material-ui/core";
import useQuery from "../../utiles/searchparams";
import PropertySection from "../../pages/Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { options, rentOptions, saleOptions } from "../../utiles/fetchApi";

type propertyType = {
  title?: string;
  price?: string;
  image?: string;
  rooms?: string;
  baths?: string;
};
function Search({ price, title, image, rooms, baths }: propertyType) {
  const [searchFilters, setSearchFilters] = useState(false);
  const [propertyArray, setPropertyArray] = useState([]);
  const query = useQuery();

  let api = options;

  if (query.get("property") === "for-sale") {
    api = saleOptions;
  }
  if (query.get("property") === "for-rent") {
    api = rentOptions;
  }

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    axios
      .request(api)
      .then(function (response) {
        setPropertyArray(response.data.hits);
      })
      .catch(function (error) {
        console.error(error);
      });
  };

  return (
    <>
      <Container>
        <Button onClick={() => setSearchFilters((prevFilters) => !prevFilters)}>
          <Title>Search Properities</Title>
          <FilterListIcon />
        </Button>
        {searchFilters && (
          <>
            <SearchFilters property={query.get("property")}></SearchFilters>
          </>
        )}
      </Container>
      <InnerContainer>
        {propertyArray.map(() => (
          <PropertySection
            image={image}
            title={title}
            price={price}
            rooms={rooms}
            baths={baths}
          ></PropertySection>
        ))}
      </InnerContainer>
    </>
  );
}

export default Search;
