import { Typography,Grid } from '@material-ui/core';
import styled from 'styled-components';
import images from "../../assets";

export const HeaderContainer = styled.div`
  display: flex;
  background:url(${images.banner});
 background-position-y: bottom;
 background-position-x: unset;
 background-size: cover;
 height: 15vw;
 margin: 0 0 4vw;

`;

export const Logo = styled.img`
  width: 20%;
  padding-left: 38vw;

`;


export const Container = styled(Grid).attrs({
  container: true,
  direction: 'column',
})<{ $fullWidth?: boolean }>`
  padding: 0 0 4vw 0;
  max-width: 1230px;

  margin: 0 auto;`;

export const BannerContainer = styled.div`
display: flex;
  flex-direction: row;
  height: 40vw;
  max-height: 380px;
  border-radius: 18px;
  overflow: hidden;`

  export const BannerContainerContent = styled.div`
  background-color: gray;
  display: flex;
  flex-direction: column;
  padding: 2vw 3vw;
  justify-content: flex-start;
  align-items: flex-start;
  height: 100%;
  width: 50%;
  box-sizing: border-box;`

  export const BannerDescription = styled.p`
  color: white;
  font-size: 1.6vw;
  line-height: 160%;`

  export const BannerTitle = styled(Typography).attrs({
  variant: 'h3',
})`
  color: white;
  font-weight: 700;
  font-size: 2.5vw;
`

export const Banner = styled.img`

  height: 100%;
  width: 50%;
  `
export const BannerButton = styled.button`

  background-color: teal;
  padding: 0 1vw 0 1vw;
  color: white;
  border-radius: 15px;
  font-size: 1.7vw;
  font-weight: 500;
  cursor: pointer;
  margin-bottom: 1.2vw`


  export const PropertySectionContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 0 3vw 4vw;
  justify-content: flex-start;
  align-items: center;
  height: 100%;
  width: 50%;
  box-sizing: border-box;`

  export const PropertySectionInnerContainer = styled.div`
  display: flex;
  flex-direction: row;
  `

  export const PropertySectionTitle = styled(Typography).attrs({
  variant: 'h6',
})`

  color: black;
  font-weight: 700;
  font-size: 2.5vw;
`
  export const PropertySectionText = styled.div` 
  margin: 5px;
  `
  export const PropertySectionImage = styled.img`
  margin: 1vw;
  border-radius: 4px;
  border-style: solid;
  border-color: teal;
  float: left;
  width:  25vw;
  height: 20vw;
  object-fit: cover;`
