import React from "react";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import ListItemText from "@mui/material/ListItemText";
import Select, { SelectProps } from "@mui/material/Select";
import { useState } from "react";
import { Container, FilterContainer } from "./styles";
import { filterData, getFilterValues } from "../../utiles/filterData";
import { useSearchParams } from "react-router-dom";

function SearchFilters({ purpose }: any) {
  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
        width: 100,
      },
    },
  };

  const [searchParams, setSearchParams] = useSearchParams();

  const [filters] = useState(filterData);

  const [filterValues, setFilterValues] = useState(
    filters.reduce(
      (acc, item) => ({
        ...acc,
        [item.queryName]: undefined,
      }),
      {} as Record<typeof filters[number]["queryName"], string | undefined>
    )
  );

  const handleChange =
    (queryName: string): SelectProps["onChange"] =>
    (e) => {
      setFilterValues((previous) => ({
        ...previous,
        [queryName]: e.target.value as string,
      }));

      setSearchParams((previous) => ({
        ...previous,
        purpose: e.target.value as string,
        // sort: e.target.value as string,
        locationExternalIDs: e.target.value as string,
        page: e.target.value as string,
        lang: e.target.value as string,
        categoryExternalID: e.target.value as string,
        roomsMax: e.target.value as string,
        rentFrequency: e.target.value as string,
        minPrice: e.target.value as string,
        maxPrice: e.target.value as string,
        areaMax: e.target.value as string,
        roomsMin: e.target.value as string,
        bathsMin: e.target.value as string,
        furnishingStatus: e.target.value as string,
      }));
    };

  return (
    <Container>
      <div>
        {purpose ? (
          <h3> Searching Properties {purpose}</h3>
        ) : (
          <h3> Searching Properties</h3>
        )}
      </div>
      <FilterContainer>
        {filters.map(({ items, placeholder, queryName }, index) => (
          <FormControl key={index} sx={{ m: 1, width: 160 }}>
            <InputLabel>{placeholder}</InputLabel>
            <Select
              value={filterValues[queryName]}
              onChange={handleChange(queryName)}
              input={<OutlinedInput />}
              MenuProps={MenuProps}
            >
              {items.map(({ value, name }) => (
                <MenuItem value={value}>
                  <ListItemText primary={name} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        ))}
      </FilterContainer>
    </Container>
  );
}
export default SearchFilters;
