import axios from "axios";
import React, { useEffect, useState } from "react";
import images from "../../assets";
import Footer from "../../components/footer";
import SearchBar from "../../components/searchBar";
import { rentOptions, saleOptions } from "../../utiles/fetchApi";
import BannerSections from "./bannerSections";
import constants from "./constants";
import Slider from "./slides";
import { HeaderContainer, Logo } from "./styles";

// export async function getStaticProps() {
//   const propertyForSale = await fetchApi(
//     `${baseURL}/properties/list?locationExternalIDs=5002&purpose=for-sale&hitsPerPage=6`
//   );
//   const propertyForRent = await fetchApi(
//     `${baseURL}/properties/list?locationExternalIDs=5002&purpose=for-rent&hitsPerPage=6`
//   );

//   return {
//     propertiesForSale: propertyForSale?.hits,
//     propertiesForRent: propertyForRent?.hits,
//   };
// }

interface SwimplySpacesItem {
  image: string;
  title: string;
}

export default function Home() {
  const [propertyName, setPropertyName] = useState();
  const [propertyCoverPhoto, setPropertyCoverPhoto] = useState();
  const [baths, setBaths] = useState();
  const [rooms, setRooms] = useState();
  const [price, setPrice] = useState();

  const [rentPropertyName, setRentPropertyName] = useState();
  const [rentPropertyCoverPhoto, setRentPropertyCoverPhoto] = useState();
  const [rentBaths, setRentBaths] = useState();
  const [rentrooms, setRentRooms] = useState();
  const [rentPrice, setRentPrice] = useState();

  useEffect(() => {
    axios
      .request(saleOptions)
      .then(function (response) {
        console.log(response.data);
        setPropertyName(response.data.hits[1].title);
        setPropertyCoverPhoto(response.data.hits[1].coverPhoto.url);
        setBaths(response.data.hits[1].baths);
        setRooms(response.data.hits[1].rooms);
        setPrice(response.data.hits[1].price);
      })
      .catch(function (error) {
        console.error(error);
      });

    axios
      .request(rentOptions)
      .then(function (response) {
        console.log(response.data);
        setRentPropertyName(response.data.hits[1].title);
        setRentPropertyCoverPhoto(response.data.hits[1].coverPhoto.url);
        setRentBaths(response.data.hits[1].baths);
        setRentRooms(response.data.hits[1].rooms);
        setRentPrice(response.data.hits[1].price);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, []);

  const items = [
    { label: rentPropertyName, value: "üçé" },
    { label: rentBaths, value: "üçå" },
    { label: rentrooms, value: "üçä" },
  ];

  // Using state to keep track of what the selected fruit is
  let [item, setFruit] = useState("‚¨áÔ∏è Select a fruit ‚¨áÔ∏è");

  // Using this function to update the state of fruit
  // whenever a new option is selected from the dropdown
  let handleFruitChange = (e: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setFruit(e.target.value);
  };

  return (
    <>
      <HeaderContainer>
        <Logo src={images.houseLogo}></Logo>
      </HeaderContainer>
      <SearchBar />
      <BannerSections
        image={images.rentBanner}
        description={constants.rentProperty.description}
        title={constants.rentProperty.title}
        button={constants.rentProperty.button}
      ></BannerSections>

      <div className="App">
        {/* Displaying the value of fruit */}
        {item}
        <br />

        <select onChange={handleFruitChange}>
          {items.map((fruit) => (
            <option value={fruit.value}>{fruit.label}</option>
          ))}
        </select>
      </div>

      <Slider
        title={rentPropertyName}
        baths={rentBaths}
        rooms={rentrooms}
        price={rentPrice}
        image={rentPropertyCoverPhoto}
      ></Slider>
      <BannerSections
        image={images.buyBanner}
        description={constants.buyProperty.description}
        title={constants.buyProperty.title}
        button={constants.buyProperty.button}
      ></BannerSections>
      <Slider
        title={propertyName}
        baths={baths}
        rooms={rooms}
        price={price}
        image={propertyCoverPhoto}
      ></Slider>

      <Footer title={"2022 Homestead"}></Footer>
    </>
  );
}
