import { IconButton, Link, MenuItem, Toolbar } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import React, { useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
import images from "../../assets";
import { Logo, Navbar, ListItem, ListContainer } from "./styles";

function NavBar() {
  const ref = useRef<HTMLDivElement>(null);

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const checkIfClickedOutside = (e: { target: any }) => {
      // If the menu is open and the clicked target is not within the menu,
      // then close the menu
      if (isMenuOpen && ref.current && !ref.current.contains(e.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", checkIfClickedOutside);

    return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", checkIfClickedOutside);
    };
  }, [isMenuOpen]);

  function useQuery() {
    const { search } = useLocation();

    return React.useMemo(() => new URLSearchParams(search), [search]);
  }

  return (
    <>
      <Navbar>
        <Toolbar>
          <Logo src={images.houseLogo}></Logo>
          <IconButton onClick={() => setIsMenuOpen((open) => !open)}>
            <MenuIcon />
          </IconButton>

          <ListContainer
            getContentAnchorEl={null}
            anchorOrigin={{ vertical: "top", horizontal: "right" }}
            MenuListProps={{
              disablePadding: true,
            }}
            open={isMenuOpen}
            onClose={() => setIsMenuOpen(false)}
          >
            <ListItem>
              <Link href="/" underline="none" color="inherit">
                HOME
              </Link>
            </ListItem>
            <ListItem>
              <Link href="/Search" underline="none" color="inherit">
                SEARCH
              </Link>
            </ListItem>
            <ListItem>
              <Link href="/Search?for-sale" underline="none" color="inherit">
                BUY
              </Link>
            </ListItem>
            <ListItem>RENT</ListItem>
          </ListContainer>
        </Toolbar>
      </Navbar>
    </>
  );
}

export default NavBar;
