import React, { useEffect, useState } from "react";
import { Container, Title } from "../../pages/Search/styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import { Button } from "@material-ui/core";
import useQuery from "../../utiles/searchparams";
import PropertySection from "../../pages/Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { saleOptions } from "../../utiles/fetchApi";

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);

  const query = useQuery();

  const [propertyArray, setPropertyArray] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    axios
      .request(saleOptions)
      .then(function (response) {
        console.log(response.data);

        setPropertyArray(...propertyArray, [
          {
            title: response.data.hits.title,
            image: response.data.hits.coverPhoto.url,
            baths: response.data.hits.baths,
            rooms: response.data.hits.rooms,
            price: response.data.hits.price,
          },
        ]);
      })
      .catch(function (error) {
        console.error(error);
      });
  };

  return (
    <>
      <Container>
        <Button onClick={() => setSearchFilters((prevFilters) => !prevFilters)}>
          <Title>Search Properities</Title>
          <FilterListIcon />
        </Button>
        {searchFilters && (
          <>
            <SearchFilters property={query.get("property")}></SearchFilters>
          </>
        )}
      </Container>
      <>
        {propertyArray.map((array) => {
          return (
            <PropertySection
              title={array.title}
              baths={array.baths}
              rooms={array.rooms}
              price={array.price}
              image={array.image}
            ></PropertySection>
          );
        })}
      </>
    </>
  );
}

export default Search;
