import {  Select } from "@material-ui/core";
import { useState, useEffect } from "react";
import { filterData, getFilterValues } from "../../utiles/filterData";
import useQuery from "../../utiles/searchparams";

function SearchFilters({ property }) {

  const [filters, setFilters] = useState({ filterData });
  const query = useQuery();

  const searchProperties = (filterValues) => {
    const path = query.get("property");
    const values = getFilterValues(filterValues)

    values.forEach((item) => {
      if(item.value && filterValues?.[item.name]) {
        query[item.name] = item.value
      }

       query.push({ pathname: path, query: query });
  })



  useEffect(() => {
    if (searchTerm !== '') {
      const fetchData = async () => {
        setLoading(true);
        const data = await fetchApi(`${baseUrl}/auto-complete?query=${searchTerm}`);
        setLoading(false);
        setLocationData(data?.hits);
      };

      fetchData();
    }
  }, [searchTerm]);


  return (
    <>
      <div>
        {property ? (
          <h3> Searching Properties {property}</h3>
        ) : (
          <h3> Searching Properties</h3>
        )}
      </div>
      <div>
        {filters.map(
          (filter) => (
            <div
              placeholder={filter.placeholder}
              onChange={(e) =>
                searchProperties({
                  [filter.queryName]: e.target.value,
                })
              }
            >
              {filter?.items?.map((item) => (
                <option value={item.value} key={item.value}>
                  {item.name}
                </option>
              ))}
            </div>
          )
        )}
      </div>
    </>
  );
}}
export default SearchFilters;
