import React, { useCallback, useEffect, useState } from "react";
import { Container, Title, InnerContainer } from "./styles";
import FilterListIcon from "@mui/icons-material/FilterList";
import { Button } from "@material-ui/core";
import useQuery from "../../utiles/searchparams";
import PropertySection from "../Home/sections";
import SearchFilters from "../../components/searchFilters";
import axios from "axios";
import { options, rentOptions, saleOptions } from "../../utiles/fetchApi";

type propertyType = { data?: any };

function Search() {
  const [searchFilters, setSearchFilters] = useState(false);
  const [propertyArray, setPropertyArray] = useState([]);
  const query = useQuery();

  let api = options;

  if (query.get("property") === "for-sale") {
    api = saleOptions;
  }
  if (query.get("property") === "for-rent") {
    api = rentOptions;
  }

  useEffect(() => {
    getData();
  }, []);

  //  useCallback()

  const getData = () => {
    axios
      .request(api)
      .then(function (response) {
        setPropertyArray(response.data.hits);
        console.log(response.data.hits);
      })
      .catch(function (error) {
        console.error(error);
      });
  };

  console.log(propertyArray);

  return (
    <>
      <Container>
        <Button onClick={() => setSearchFilters((prevFilters) => !prevFilters)}>
          <Title>Search Properities</Title>
          <FilterListIcon />
        </Button>
        {searchFilters && (
          <>
            <SearchFilters property={query.get("property")}></SearchFilters>
          </>
        )}
      </Container>
      <InnerContainer>
        {propertyArray.map(({ data }: propertyType) => (
          <>
            <PropertySection
              image={data?.image}
              title={data?.title}
              price={data?.price ?? 0}
              rooms={data?.rooms}
              baths={data?.baths}
            ></PropertySection>
          </>
        ))}
      </InnerContainer>
    </>
  );
}

export default Search;
